CREATE TABLE "public"."sigesp_plan_unico_caif"(
"codigo_caif" char(25) NOT NULL ,
"sig_cuenta" char(25) 
)  WITHOUT OIDS;

-- ----------------------------
-- Records 
-- ----------------------------


/*se crea el campo de codigo caif en la tabla sigesp_plan_unico_re.*/

alter table sigesp_plan_unico_re add codcaif varchar(25);
	



/*se copian los datos  desde la tabla temporal al campo codcaif de la tabla sigesp_plan_unico_re*/

update sigesp_plan_unico_re 
set codcaif=sigesp_plan_unico_caif.codigo_caif 
from sigesp_plan_unico_caif 
where sigesp_plan_unico_re.sig_cuenta=sigesp_plan_unico_caif.sig_cuenta;


